import { Component, OnInit } from '@angular/core';
import { Observable } from 'rxjs';
import { Recarga } from 'src/app/domain/recarga';
import { RecargaService } from 'src/app/services/recarga.service';

@Component({
  selector: 'app-recargas-mostrar',
  templateUrl: './recargas-mostrar.component.html',
  styleUrls: ['./recargas-mostrar.component.css']
})
export class RecargasMostrarComponent implements OnInit{
  recargas$!: Observable<Recarga[]>;

constructor(private recargaService: RecargaService) { }

ngOnInit(): void {
  this.recargas$ = this.recargaService.getRecargas();
}

}
<div>
  <label for="id">ID:</label>
  <input type="text" id="id" name="id" [(ngModel)]="usuario.id"><br><br>

  <label for="username">Username:</label>
  <input type="text" id="username" name="username" [(ngModel)]="usuario.username"><br><br>

  <label for="firstName">First Name:</label>
  <input type="text" id="firstName" name="firstName" [(ngModel)]="usuario.firstName"><br><br>

  <label for="lastName">Last Name:</label>
  <input type="text" id="lastName" name="lastName" [(ngModel)]="usuario.lastName"><br><br>

  <label for="email">Email:</label>
  <input type="text" id="email" name="email" [(ngModel)]="usuario.email"><br><br>

  <label for="password">Password:</label>
  <input type="password" id="password" name="password" [(ngModel)]="usuario.password"><br><br>

  <label for="phone">Phone:</label>
  <input type="text" id="phone" name="phone" [(ngModel)]="usuario.phone"><br><br>

  <label for="userStatus">User Status:</label>
  <input type="number" id="userStatus" name="userStatus" [(ngModel)]="usuario.userStatus"><br><br>

  <button (click)="enviarUsuario()">Enviar Usuario</button>
</div>
