import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Cliente } from 'src/app/domain/cliente';
import { ClienteService } from 'src/app/services/cliente.service';
import { AbstractControl, ValidationErrors, ValidatorFn } from '@angular/forms';


@Component({
  selector: 'app-cliente',
  templateUrl: './cliente.component.html',
  styleUrls: ['./cliente.component.css']
})
export class ClienteComponent {
  signUpForm: FormGroup;

  constructor(private formBuilder: FormBuilder, private clienteService: ClienteService) {
    this.signUpForm = this.formBuilder.group({
      nombre: ['', [Validators.required, Validators.minLength(4)]], // Corregido
      dni: ['', [Validators.required, Validators.minLength(10)]], 
      saldo: [0, [Validators.required, Validators.min(0)]],
    });
  }


  onSubmit() {
    if (this.signUpForm.valid) {
      const cliente: Cliente = this.signUpForm.value;
      this.clienteService.saveClientes(cliente).subscribe(
        response => {
          console.log('Registro exitoso', response);
          alert('Registro exitoso')
        },
        error => {
          console.error('Error al registrar el cliente', error);
          // Aquí puedes manejar errores, como mostrar un mensaje al usuario
        }
      );
    }
  }
}
<div *ngIf="clienteData">
  <h2>Detalles del Cliente</h2>
  <p>ID: {{ clienteData.id }}</p>
  <p>Nombre de usuario: {{ clienteData.username }}</p>
  <p>Nombre: {{ clienteData.firstName }}</p>
  <p>Apellido: {{ clienteData.lastName }}</p>
  <p>Email: {{ clienteData.email }}</p>
  <p>Teléfono: {{ clienteData.phone }}</p>
  <p>Estado de usuario: {{ clienteData.userStatus }}</p>
</div>
